C++ program for Implementing Counting Sort

#include <iostream>
#include <vector>

void countingSort(std::vector<int>& arr, int k) {
// Create an array to store the count of each element
std::vector<int> count(k + 1, 0);

// Count the occurrences of each element
for (int element : arr) {
count[element]++;
}

// Calculate the cumulative sum of the count array
for (int i = 1; i <= k; ++i) {
count[i] += count[i - 1];
}

// Create an output array to store the sorted elements
std::vector<int> sorted(arr.size());

// Place each element in its correct position according to the count array
for (int i = arr.size() - 1; i >= 0; --i) {
sorted[count[arr[i]] - 1] = arr[i];
count[arr[i]]--;
}

// Copy the sorted elements back to the original array
for (int i = 0; i < arr.size(); ++i) {
arr[i] = sorted[i];
}
}

int main() {
// Sample input
std::vector<int> arr = {2, 1, 4, 3, 1, 2};

// Maximum key value
int k = 4;

// Sort the array
countingSort(arr, k);

// Print the sorted array
std::cout << "Sorted array: ";
for (int element : arr) {
std::cout << element << " ";
}
std::cout << std::endl;

