std::pair<std::string, int> combineWords(const std::vector<std::string>& palabras) {
std::vector<WordInfo> palabra_infos;
int letras_min_compartidas = INT_MAX;

// Calcular letras compartidas para cada par de palabras.
para (tamaño_t i = 0; i < palabras.tamaño() - 1; ++i) {
for (tamaño_t j = i + 1; j < palabras.tamaño(); ++j) {
std::vector<std::string> combinaciones = generarCombinaciones(palabras[i], palabras[j]);
int letras_compartidas = combinaciones.tamaño();
letras_min_compartidas = std::min(letras_min_compartidas, letras_compartidas);
word_infos.push_back({palabras[i], letras_compartidas});
word_infos.push_back({palabras[j], letras_compartidas});
}
}

// Ordenar palabras por letras compartidas
std::sort(word_infos.begin(), word_infos.end(), compareBySharedLetters);

// Combina palabras basadas en letras compartidas
std::cadena cadena_combinada;
para (const auto& word_info: word_infos) {
